def getRoleId():
  role = getString(Cmd.OB_ROLE_ID)
  cg = UID_PATTERN.match(role)
  if cg:
    roleId = cg.group(1)
  else:
    roleId = roleid_from_role(role)
    if not roleId:
      invalidChoiceExit(GM.Globals[GM.MAP_ROLE_NAME_TO_ID], True)
  return (role, roleId)

