  calendarEventEntity = getCalendarEventEntity()
  doIt, sendNotifications = _getCalendarDeleteEventOptions()
  _purgeCalendarEvents(None, None, cal, calIds, len(calIds), calendarEventEntity, doIt, sendNotifications, False)

