def cleanFileIDsList(fileIdEntity, fileIds):
  def _getFileIdFromURL(fileId):
    loc = fileId.find(u'/d/')
    if loc > 0:
      fileId = fileId[loc+3:]
      loc = fileId.find(u'/')
      return fileId[:loc] if loc != -1 else fileId
    loc = fileId.find(u'?id=')
    if loc > 0:
      fileId = fileId[loc+4:]
      loc = fileId.find(u'&')
      return fileId[:loc] if loc != -1 else fileId
    loc = fileId.find(u'/files/')
    if loc > 0:
      fileId = fileId[loc+7:]
      loc = fileId.find(u'&')
      return fileId[:loc] if loc != -1 else fileId
    loc = fileId.find(u'/folders/')
    if loc > 0:
      fileId = fileId[loc+9:]
      loc = fileId.find(u'&')
      return fileId[:loc] if loc != -1 else fileId
    return None

  fileIdEntity[u'list'] = []
  fileIdEntity[u'root'] = []
  i = 0
  for fileId in fileIds:
    if fileId[:8].lower() == u'https://' or fileId[:7].lower() == u'http://':
      fileId = _getFileIdFromURL(fileId)
      if fileId is None:
        continue
    elif fileId.lower() == u'root':
      fileIdEntity[u'root'].append(i)
      fileId = fileId.lower()
    fileIdEntity[u'list'].append(fileId)
    i += 1

TITLE_QUERY_PATTERN = re.compile(r'title((?: *!?=)|(?: +contains))', flags=re.IGNORECASE)

def _mapDrive2QueryToDrive3(query):
  if query:
    query = TITLE_QUERY_PATTERN.sub(r'name\1', query).replace(u'modifiedDate', VX_MODIFIED_TIME).replace(u'lastViewedByMeDate', VX_VIEWED_BY_ME_TIME)
  return query

def escapeDriveFileName(filename):
  if filename.find(u"'") == -1 and filename.find(u'\\') == -1:
    return filename
  encfilename = u''
  for c in filename:
    if c == u"'":
      encfilename += u"\\'"
    elif c == u'\\':
      encfilename += u'\\\\'
    else:
      encfilename += c
  return encfilename

