  _finalizeMessageSelectParameters(parameters, False)
  if csvFormat:
    if countsOnly:
      sortTitles = [u'User', parameters[u'listType']]
      titles, csvRows = initializeTitlesCSVfile(sortTitles)
    else:
      sortTitles = [u'User', u'threadId', u'id']
